INSERT INTO restaurant_reviews (user_id, restaurant_id, content) VALUES
                                                                     (1, 1, 'Bardzo smaczne dania, obsługa na wysokim poziomie. Polecam!'),
                                                                     (2, 1, 'Niestety jedzenie było zimne, co mocno wpłynęło na doświadczenie.');
INSERT INTO restaurant_reviews (user_id, restaurant_id, content) VALUES
                                                                     (3, 2, 'Fantastyczne miejsce, dania przepyszne, a obsługa miła i profesjonalna.'),
                                                                     (1, 2, 'Byłem tu kilka razy i zawsze wychodzę zadowolony.');
INSERT INTO restaurant_reviews (user_id, restaurant_id, content) VALUES
                                                                     (2, 3, 'Przyjemne miejsce na kolację, choć jedzenie mogłoby być lepsze.'),
                                                                     (3, 3, 'Obsługa bardzo pomocna, ale dania średniego smaku.');
INSERT INTO restaurant_reviews (user_id, restaurant_id, content) VALUES
                                                                     (1, 4, 'Smakowite dania, a obsługa sprawiła, że czuliśmy się mile widziani.'),
                                                                     (2, 4, 'Trochę rozczarowany, ale obsługa była zaskakująco dobra.');
INSERT INTO restaurant_reviews (user_id, restaurant_id, content) VALUES
                                                                     (3, 5, 'Jedzenie było niesamowite, choć obsługa mogłaby być bardziej uwaga.'),
