INSERT INTO restaurant_reviews (user_id, restaurant_id, content) VALUES
                                                                     (2, 6, 'Fantastyczne doświadczenie kulinarne, polecam każdemu.'),
                                                                     (3, 6, 'Obsługa była nieco niemiła, ale jedzenie zrekompensowało.');
INSERT INTO restaurant_reviews (user_id, restaurant_id, content) VALUES
                                                                     (1, 7, 'Nie mogę się doczekać kolejnej wizyty! Atmosfera rewelacyjna.'),
